mapper.select:
SELECT ID, BANK_ID, FULL_NAME, BIRTH_DATE, ADDRESS_ID
FROM customer
WHERE ID = #{id}

mapper.update:
UPDATE customer
SET BANK_ID    = #{bankId},
FULL_NAME  = #{fullName},
BIRTH_DATE = #{birthDate},
ADDRESS_ID = #{addressId}
WHERE ID = #{id}

